const tempoMin = parseInt($input.first().json.body.tempoMin);
const tempoMax = parseInt($input.first().json.body.tempoMax);

if (isNaN(tempoMin) || isNaN(tempoMax) || tempoMin > tempoMax) {
  throw new Error('Preencha os campos de tempo corretamente!');
}

const delay = Math.floor(Math.random() * (tempoMax - tempoMin + 1)) + tempoMin;

// Retorne o resultado como um array de objetos
return [
  {
    json: {
      delay
    }
  }
];
